-- Seed de 80 perguntas para o Teste das Cores (5 Dinâmicas Humanas)
ON CONFLICT (question_number) DO UPDATE
SET prompt = EXCLUDED.prompt,
	option_azul = EXCLUDED.option_azul,
	option_rosa = EXCLUDED.option_rosa,
	option_amarelo = EXCLUDED.option_amarelo,
	option_verde = EXCLUDED.option_verde,
	option_branco = EXCLUDED.option_branco,
	active = EXCLUDED.active,
	updated_at = NOW();
